tinymce.PluginManager.add("radomparticipantcustomtext", function (editor) {
    editor.addButton("radomparticipantcustomtext", {
        text: "Добавить текст с указанием пола",
        icon: false,
        tooltip : "Добавить текст с указанием пола",
        onclick: function() {
            var selection = editor.selection,
                dom = editor.dom,
                selectedElm,
                anchorElm;

            // Focus the editor since selection is lost on WebKit in inline mode
            editor.focus();

            $("#radomParticipantCustomTextWindow").modal({backdrop: false, keyboard: false});
        }
    });
});