tinymce.PluginManager.add("radomparticipantfilter", function (editor) {
    editor.addButton("radomparticipantfilter", {
        text: "Добавить [Источник данных:Поле]",
        icon: false,
        tooltip : "Добавить блок автоподстановки",
        onclick: function() {
            var selection = editor.selection,
                dom = editor.dom,
                selectedElm,
                anchorElm;

            // Focus the editor since selection is lost on WebKit in inline mode
            editor.focus();

            $("#radomParticipantFilterWindow").modal({backdrop: false, keyboard: false});
        }
    });
});