tinymce.PluginManager.add("radomsystemfields", function (editor) {
    editor.addButton("radomsystemfields", {
        text: "Добавить системное поле",
        icon: false,
        tooltip : "Добавить системное поле",
        onclick: function() {
            var selection = editor.selection,
                dom = editor.dom,
                selectedElm,
                anchorElm;

            // Focus the editor since selection is lost on WebKit in inline mode
            editor.focus();

            $("#radomSystemFieldsWindow").modal({backdrop: false, keyboard: false});
        }
    });
});